$(document).ready(function() {
    // Tooltips Initialization
    $(function() {
        $('[data-toggle="tooltip"]').tooltip()
    })
});

function save() {
    $.ajax({
        url: "./send.php",
        type: "POST",
        data: $("form").serializeArray(),
        success: (res) => {
            // console.log(res)
            alert(res);
            $("form")[0].reset();

                $('.btn-primary').addClass('btn-success').removeClass('btn-primary');
                $('.primary-color').addClass('success-color').removeClass('primary-color');

                $('.btn-danger').addClass('btn-success').removeClass('btn-danger');
                $('.danger-color').addClass('success-color').removeClass('danger-color');
        },
        error: (e) => {
            // console.log(e);
            alert(e.responseText)

                $('.btn-primary').addClass('btn-danger').removeClass('btn-primary');
                $('.primary-color').addClass('danger-color').removeClass('primary-color');

                $('.btn-success').addClass('btn-danger').removeClass('btn-success');
                $('.success-color').addClass('danger-color').removeClass('success-color');
        }
    });
}
